    public Country getRandomCountry(Context context) throws OpenMeteoException {
        Country randomCountry = COUNTRIES.get(RANDOM.nextInt(COUNTRIES.size()));
        int randomCountryCurrentWeather;
        try {
            randomCountryCurrentWeather = OPEN_METEO_SERVICE.getCurrentWeather(randomCountry);
        } catch (OpenMeteoException e) {
            throw e;
        }
        randomCountry.setCurrentWeather(randomCountryCurrentWeather);
        return randomCountry;
    }

